import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, boolean, pgEnum } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Enums
export const userRoleEnum = pgEnum("user_role", ["super_admin", "admin", "branch_admin"]);
export const leaveTypeEnum = pgEnum("leave_type", ["yillik", "hafta_tatili", "resmi_tatil", "hastalik", "dogum", "babalik", "evlilik", "olum", "mazeret", "ucretsiz"]);
export const documentTypeEnum = pgEnum("document_type", ["health_report", "criminal_record", "other"]);
export const shiftTypeEnum = pgEnum("shift_type", ["sabah", "oglen", "aksam", "gece"]);

// Users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  phone: text("phone").notNull().unique(),
  password: text("password").notNull(),
  name: text("name").notNull(),
  role: userRoleEnum("role").notNull().default("branch_admin"),
  branchId: varchar("branch_id"),
  isActive: boolean("is_active").notNull().default(true),
  twoFactorCode: text("two_factor_code"),
  twoFactorExpiry: timestamp("two_factor_expiry"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Branches table
export const branches = pgTable("branches", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  address: text("address"),
  phone: text("phone"),
  parentBranchId: varchar("parent_branch_id"),
  managerId: varchar("manager_id"), // Personnel ID of the branch manager
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Personnel table
export const personnel = pgTable("personnel", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  employeeNumber: text("employee_number").notNull().unique(),
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  phone: text("phone").notNull(),
  email: text("email"),
  nationalId: text("national_id").notNull().unique(),
  birthDate: timestamp("birth_date"),
  address: text("address"),
  position: text("position").notNull(),
  department: text("department"),
  branchId: varchar("branch_id").notNull(),
  startDate: timestamp("start_date").notNull(),
  salary: integer("salary"),
  profilePhotoUrl: text("profile_photo_url"),
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Shifts table
export const shifts = pgTable("shifts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  type: shiftTypeEnum("type").notNull(),
  startTime: text("start_time").notNull(), // HH:MM format
  endTime: text("end_time").notNull(), // HH:MM format
  branchId: varchar("branch_id").notNull(),
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

// Personnel Shifts table
export const personnelShifts = pgTable("personnel_shifts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull(),
  shiftId: varchar("shift_id").notNull(),
  assignedDate: timestamp("assigned_date").notNull(),
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

// Attendance table
export const attendance = pgTable("attendance", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull(),
  checkIn: timestamp("check_in"),
  checkOut: timestamp("check_out"),
  date: timestamp("date").notNull().default(sql`CURRENT_DATE`),
  location: text("location"),
  qrCode: text("qr_code"),
  notes: text("notes"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

// Leave Requests table
export const leaveRequests = pgTable("leave_requests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull(),
  type: leaveTypeEnum("type").notNull(),
  startDate: timestamp("start_date").notNull(),
  endDate: timestamp("end_date").notNull(),
  reason: text("reason"),
  status: text("status").notNull().default("pending"), // pending, approved, rejected
  approvedBy: varchar("approved_by"),
  approvedAt: timestamp("approved_at"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

// Personnel Documents table
export const personnelDocuments = pgTable("personnel_documents", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull(),
  type: documentTypeEnum("type").notNull(),
  title: text("title").notNull(),
  fileName: text("file_name").notNull(),
  filePath: text("file_path").notNull(),
  expiryDate: timestamp("expiry_date"),
  isValid: boolean("is_valid").notNull().default(true),
  notes: text("notes"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Personnel Financial Info table
export const personnelFinancialInfo = pgTable("personnel_financial_info", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull().unique(),
  bankName: text("bank_name"),
  iban: text("iban"),
  accountNumber: text("account_number"),
  taxNumber: text("tax_number"),
  socialSecurityNumber: text("social_security_number"),
  emergencyContactName: text("emergency_contact_name"),
  emergencyContactPhone: text("emergency_contact_phone"),
  emergencyContactRelation: text("emergency_contact_relation"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Personnel Education table
export const personnelEducation = pgTable("personnel_education", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull(),
  educationLevel: text("education_level").notNull(), // ilkokul, ortaokul, lise, universite, yuksek_lisans, doktora
  schoolName: text("school_name").notNull(),
  department: text("department"),
  graduationYear: integer("graduation_year"),
  isCompleted: boolean("is_completed").notNull().default(true),
  notes: text("notes"),
  createdAt: timestamp("created_at").notNull().default(sql`gen_random_uuid()`),
});

// Personnel Leave Balances table
export const personnelLeaveBalances = pgTable("personnel_leave_balances", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  personnelId: varchar("personnel_id").notNull(),
  leaveType: leaveTypeEnum("leave_type").notNull(),
  totalDays: integer("total_days").notNull().default(0),
  usedDays: integer("used_days").notNull().default(0),
  remainingDays: integer("remaining_days").notNull().default(0),
  year: integer("year").notNull(),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

// Relations
export const usersRelations = relations(users, ({ one }) => ({
  branch: one(branches, {
    fields: [users.branchId],
    references: [branches.id],
  }),
}));

export const branchesRelations = relations(branches, ({ one, many }) => ({
  parentBranch: one(branches, {
    fields: [branches.parentBranchId],
    references: [branches.id],
  }),
  manager: one(personnel, {
    fields: [branches.managerId],
    references: [personnel.id],
  }),
  subBranches: many(branches),
  personnel: many(personnel),
  users: many(users),
  shifts: many(shifts),
}));

export const personnelRelations = relations(personnel, ({ one, many }) => ({
  branch: one(branches, {
    fields: [personnel.branchId],
    references: [branches.id],
  }),
  shifts: many(personnelShifts),
  attendance: many(attendance),
  leaveRequests: many(leaveRequests),
  documents: many(personnelDocuments),
  financialInfo: one(personnelFinancialInfo),
  education: many(personnelEducation),
  leaveBalances: many(personnelLeaveBalances),
}));

export const shiftsRelations = relations(shifts, ({ one, many }) => ({
  branch: one(branches, {
    fields: [shifts.branchId],
    references: [branches.id],
  }),
  personnel: many(personnelShifts),
}));

export const personnelShiftsRelations = relations(personnelShifts, ({ one }) => ({
  personnel: one(personnel, {
    fields: [personnelShifts.personnelId],
    references: [personnel.id],
  }),
  shift: one(shifts, {
    fields: [personnelShifts.shiftId],
    references: [shifts.id],
  }),
}));

export const attendanceRelations = relations(attendance, ({ one }) => ({
  personnel: one(personnel, {
    fields: [attendance.personnelId],
    references: [personnel.id],
  }),
}));

export const leaveRequestsRelations = relations(leaveRequests, ({ one }) => ({
  personnel: one(personnel, {
    fields: [leaveRequests.personnelId],
    references: [personnel.id],
  }),
  approver: one(users, {
    fields: [leaveRequests.approvedBy],
    references: [users.id],
  }),
}));

export const personnelDocumentsRelations = relations(personnelDocuments, ({ one }) => ({
  personnel: one(personnel, {
    fields: [personnelDocuments.personnelId],
    references: [personnel.id],
  }),
}));

export const personnelFinancialInfoRelations = relations(personnelFinancialInfo, ({ one }) => ({
  personnel: one(personnel, {
    fields: [personnelFinancialInfo.personnelId],
    references: [personnel.id],
  }),
}));

export const personnelEducationRelations = relations(personnelEducation, ({ one }) => ({
  personnel: one(personnel, {
    fields: [personnelEducation.personnelId],
    references: [personnel.id],
  }),
}));

export const personnelLeaveBalancesRelations = relations(personnelLeaveBalances, ({ one }) => ({
  personnel: one(personnel, {
    fields: [personnelLeaveBalances.personnelId],
    references: [personnel.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  twoFactorCode: true,
  twoFactorExpiry: true,
});

export const insertBranchSchema = createInsertSchema(branches).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertPersonnelSchema = createInsertSchema(personnel).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertShiftSchema = createInsertSchema(shifts).omit({
  id: true,
  createdAt: true,
});

export const insertLeaveRequestSchema = createInsertSchema(leaveRequests).omit({
  id: true,
  createdAt: true,
  approvedBy: true,
  approvedAt: true,
});

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type Branch = typeof branches.$inferSelect;
export type InsertBranch = z.infer<typeof insertBranchSchema>;
export type Personnel = typeof personnel.$inferSelect;
export type InsertPersonnel = z.infer<typeof insertPersonnelSchema>;
export type Shift = typeof shifts.$inferSelect;
export type InsertShift = z.infer<typeof insertShiftSchema>;
export type LeaveRequest = typeof leaveRequests.$inferSelect;
export type InsertLeaveRequest = z.infer<typeof insertLeaveRequestSchema>;
export type Attendance = typeof attendance.$inferSelect;

export const insertPersonnelDocumentSchema = createInsertSchema(personnelDocuments).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});
export type InsertPersonnelDocument = z.infer<typeof insertPersonnelDocumentSchema>;
export type PersonnelDocument = typeof personnelDocuments.$inferSelect;

export const insertPersonnelFinancialInfoSchema = createInsertSchema(personnelFinancialInfo).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});
export type InsertPersonnelFinancialInfo = z.infer<typeof insertPersonnelFinancialInfoSchema>;
export type PersonnelFinancialInfo = typeof personnelFinancialInfo.$inferSelect;

export const insertPersonnelEducationSchema = createInsertSchema(personnelEducation).omit({
  id: true,
  createdAt: true,
});
export type InsertPersonnelEducation = z.infer<typeof insertPersonnelEducationSchema>;
export type PersonnelEducation = typeof personnelEducation.$inferSelect;

export const insertPersonnelLeaveBalanceSchema = createInsertSchema(personnelLeaveBalances).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});
export type InsertPersonnelLeaveBalance = z.infer<typeof insertPersonnelLeaveBalanceSchema>;
export type PersonnelLeaveBalance = typeof personnelLeaveBalances.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider } from "@/hooks/use-auth";
import { ProtectedRoute } from "./lib/protected-route";
import NotFound from "@/pages/not-found";
import AuthPage from "@/pages/auth-page";
import Dashboard from "@/pages/dashboard";
import PersonnelManagement from "@/pages/personnel-management";
import BranchManagement from "@/pages/branch-management";
import ShiftManagement from "@/pages/shift-management";
import LeaveManagement from "@/pages/leave-management";
import AttendanceTracking from "@/pages/attendance-tracking";
import QRControl from "@/pages/qr-control";
import Reports from "@/pages/reports";
import SystemSettings from "@/pages/system-settings";
import CalendarPage from "@/pages/calendar";

function Router() {
  return (
    <Switch>
      <ProtectedRoute path="/" component={Dashboard} />
      <ProtectedRoute path="/dashboard" component={Dashboard} />
      <ProtectedRoute path="/personnel" component={PersonnelManagement} />
      <ProtectedRoute path="/branches" component={BranchManagement} />
      <ProtectedRoute path="/shifts" component={ShiftManagement} />
      <ProtectedRoute path="/leave" component={LeaveManagement} />
      <ProtectedRoute path="/attendance" component={AttendanceTracking} />
      <ProtectedRoute path="/qr-control" component={QRControl} />
      <ProtectedRoute path="/reports" component={Reports} />
      <ProtectedRoute path="/settings" component={SystemSettings} />
      <ProtectedRoute path="/calendar" component={CalendarPage} />
      <Route path="/auth" component={AuthPage} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <AuthProvider>
          <Toaster />
          <Router />
        </AuthProvider>
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

