export interface NetgsmConfig {
  username: string;
  password: string;
  title: string;
}

export class NetgsmService {
  private config: NetgsmConfig;

  constructor() {
    this.config = {
      username: process.env.NETGSM_USERNAME || '',
      password: process.env.NETGSM_PASSWORD || '',
      title: process.env.NETGSM_TITLE || 'PTS',
    };
  }

  async sendSMS(phone: string, message: string): Promise<boolean> {
    try {
      const url = 'https://api.netgsm.com.tr/sms/send/get';
      const params = new URLSearchParams({
        usercode: this.config.username,
        password: this.config.password,
        gsmno: phone.replace(/\D/g, ''), // Remove non-digits
        message: message,
        msgheader: this.config.title,
      });

      const response = await fetch(`${url}?${params}`);
      const result = await response.text();

      // Netgsm returns response codes: 
      // "00" or "01" means success
      // Other codes mean error
      return result.startsWith('00') || result.startsWith('01');
    } catch (error) {
      console.error('Netgsm SMS gönderme hatası:', error);
      return false;
    }
  }

  async send2FA(phone: string, code: string): Promise<boolean> {
    const message = `PTS giriş doğrulama kodunuz: ${code}. Bu kodu kimseyle paylaşmayın.`;
    return this.sendSMS(phone, message);
  }
}

export const netgsmService = new NetgsmService();

client/src/hooks/use-auth.tsx.content:
import { createContext, ReactNode, useContext } from "react";
import {
  useQuery,
  useMutation,
  UseMutationResult,
} from "@tanstack/react-query";
import { insertUserSchema, User as SelectUser, InsertUser } from "@shared/schema";
import { getQueryFn, apiRequest, queryClient } from "../lib/queryClient";
import { useToast } from "@/hooks/use-toast";

type AuthContextType = {
  user: SelectUser | null;
  isLoading: boolean;
  error: Error | null;
  loginMutation: UseMutationResult<any, Error, LoginData>;
  logoutMutation: UseMutationResult<void, Error, void>;
  registerMutation: UseMutationResult<SelectUser, Error, InsertUser>;
  verify2FAMutation: UseMutationResult<SelectUser, Error, TwoFactorData>;
};

type LoginData = Pick<InsertUser, "phone" | "password">;
type TwoFactorData = { userId: string; code: string };

export const AuthContext = createContext<AuthContextType | null>(null);

export function AuthProvider({ children }: { children: ReactNode }) {
  const { toast } = useToast();
  const {
    data: user,
    error,
    isLoading,
  } = useQuery<SelectUser | undefined, Error>({
    queryKey: ["/api/user"],
    queryFn: getQueryFn({ on401: "returnNull" }),
  });

  const loginMutation = useMutation({
    mutationFn: async (credentials: LoginData) => {
      const res = await apiRequest("POST", "/api/login", credentials);
      return await res.json();
    },
    onSuccess: (response: any) => {
      if (response.requiresTwoFactor) {
        toast({
          title: "Doğrulama Kodu Gönderildi",
          description: "Telefon numaranıza gönderilen kodu girin",
        });
      } else {
        queryClient.setQueryData(["/api/user"], response);
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Giriş Başarısız",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const verify2FAMutation = useMutation({
    mutationFn: async (data: TwoFactorData) => {
      const res = await apiRequest("POST", "/api/verify-2fa", data);
      return await res.json();
    },
    onSuccess: (user: SelectUser) => {
      queryClient.setQueryData(["/api/user"], user);
      toast({
        title: "Giriş Başarılı",
        description: "Hoş geldiniz!",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Doğrulama Başarısız",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (credentials: InsertUser) => {
      const res = await apiRequest("POST", "/api/register", credentials);
      return await res.json();
    },
    onSuccess: (user: SelectUser) => {
      queryClient.setQueryData(["/api/user"], user);
      toast({
        title: "Kayıt Başarılı",
        description: "Hesabınız oluşturuldu",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Kayıt Başarısız",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const logoutMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", "/api/logout");
    },
    onSuccess: () => {
      queryClient.setQueryData(["/api/user"], null);
      toast({
        title: "Çıkış Yapıldı",
        description: "Güvenli bir şekilde çıkış yaptınız",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Çıkış Hatası",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  return (
    <AuthContext.Provider
      value={{
        user: user ?? null,
        isLoading,
        error,
        loginMutation,
        logoutMutation,
        registerMutation,
        verify2FAMutation,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

