import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Calendar, Clock, User, Search, Download, Filter } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import MainLayout from "@/components/layout/main-layout";
import type { Attendance, Personnel, Branch } from "@shared/schema";

export default function AttendanceTracking() {
  const [searchTerm, setSearchTerm] = useState("");
  const [dateFilter, setDateFilter] = useState("today");
  const [branchFilter, setBranchFilter] = useState("all");

  const { data: attendance = [], isLoading } = useQuery<Attendance[]>({
    queryKey: ["/api/attendance", { date: dateFilter, branch: branchFilter }],
  });

  const { data: personnel = [] } = useQuery<Personnel[]>({
    queryKey: ["/api/personnel"],
  });

  const { data: branches = [] } = useQuery<Branch[]>({
    queryKey: ["/api/branches"],
  });

  const filteredAttendance = attendance.filter((record) => {
    const person = personnel.find(p => p.id === record.personnelId);
    if (!person) return false;

    const fullName = `${person.firstName} ${person.lastName}`;
    const searchMatch = fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                       person.employeeNumber.includes(searchTerm);

    return searchMatch;
  });

  const calculateWorkHours = (checkIn?: Date | string, checkOut?: Date | string) => {
    if (!checkIn || !checkOut) return "Devam ediyor";

    const start = new Date(checkIn);
    const end = new Date(checkOut);
    const diffMs = end.getTime() - start.getTime();
    const hours = Math.floor(diffMs / (1000 * 60 * 60));
    const minutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));

    return `${hours}s ${minutes}dk`;
  };

  const stats = {
    total: filteredAttendance.length,
    present: filteredAttendance.filter(a => a.checkInTime && !a.checkOutTime).length,
    completed: filteredAttendance.filter(a => a.checkInTime && a.checkOutTime).length,
    late: filteredAttendance.filter(a => {
      if (!a.checkInTime) return false;
      const checkIn = new Date(a.checkInTime);
      const hour = checkIn.getHours();
      const minute = checkIn.getMinutes();
      return hour > 9 || (hour === 9 && minute > 0); // 09:00'dan sonra geç
    }).length
  };

  if (isLoading) {
    return (
      <MainLayout>
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-muted-foreground">Devam kayıtları yükleniyor...</p>
          </div>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">Devam Takibi</h1>
            <p className="text-muted-foreground">Detaylı personel giriş-çıkış kayıtları</p>
          </div>
          <Button data-testid="button-export-attendance">
            <Download className="mr-2 h-4 w-4" />
            Rapor İndir
          </Button>
        </div>

        {/* Stats Cards */}
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Toplam Kayıt</CardTitle>
              <User className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-attendance">{stats.total}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">İş Yerinde</CardTitle>
              <Clock className="h-4 w-4 text-green-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600" data-testid="text-present-count">{stats.present}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Tamamlanan</CardTitle>
              <Calendar className="h-4 w-4 text-blue-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-600" data-testid="text-completed-count">{stats.completed}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Geç Gelen</CardTitle>
              <Clock className="h-4 w-4 text-red-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600" data-testid="text-late-count">{stats.late}</div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Personel adı veya sicil numarası ile ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-8"
              data-testid="input-search-attendance"
            />
          </div>
          <Select value={dateFilter} onValueChange={setDateFilter}>
            <SelectTrigger className="w-[180px]" data-testid="select-date-filter">
              <SelectValue placeholder="Tarih filtresi" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="today">Bugün</SelectItem>
              <SelectItem value="yesterday">Dün</SelectItem>
              <SelectItem value="this-week">Bu Hafta</SelectItem>
              <SelectItem value="last-week">Geçen Hafta</SelectItem>
              <SelectItem value="this-month">Bu Ay</SelectItem>
              <SelectItem value="last-month">Geçen Ay</SelectItem>
            </SelectContent>
          </Select>
          <Select value={branchFilter} onValueChange={setBranchFilter}>
            <SelectTrigger className="w-[180px]" data-testid="select-branch-filter">
              <SelectValue placeholder="Şube filtresi" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">Tüm Şubeler</SelectItem>
              {branches.map((branch) => (
                <SelectItem key={branch.id} value={branch.id}>
                  {branch.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Attendance Records */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Calendar className="mr-2 h-5 w-5" />
              Devam Kayıtları
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {filteredAttendance.length === 0 ? (
                <div className="text-center py-8">
                  <Calendar className="mx-auto h-12 w-12 text-muted-foreground" />
                  <h3 className="mt-4 text-lg font-semibold">Kayıt bulunamadı</h3>
                  <p className="text-muted-foreground">
                    {searchTerm ? "Arama kriterlerinize uygun" : "Seçilen filtrelere uygun"} devam kaydı bulunmuyor.
                  </p>
                </div>
              ) : (
                <div className="space-y-3">
                  {filteredAttendance.map((record) => {
                    const person = personnel.find(p => p.id === record.personnelId);
                    const branch = branches.find(b => b.id === person?.branchId);
                    if (!person) return null;

                    const fullName = `${person.firstName} ${person.lastName}`;
                    const isPresent = record.checkInTime && !record.checkOutTime;
                    const isLate = record.checkInTime ? 
                      new Date(record.checkInTime).getHours() > 9 || 
                      (new Date(record.checkInTime).getHours() === 9 && new Date(record.checkInTime).getMinutes() > 0) 
                      : false;

                    return (
                      <div 
                        key={record.id} 
                        className="border rounded-lg p-4"
                        data-testid={`card-attendance-record-${record.id}`}
                      >
                        <div className="flex items-center justify-between mb-3">
                          <div className="flex items-center space-x-4">
                            <div>
                              <h3 className="font-semibold">{fullName}</h3>
                              <p className="text-sm text-muted-foreground">
                                {person.employeeNumber} • {person.position} • {branch?.name || "Atanmamış"}
                              </p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-2">
                            {isPresent && (
                              <Badge className="bg-green-500 hover:bg-green-600">
                                İş Yerinde
                              </Badge>
                            )}
                            {isLate && (
                              <Badge variant="destructive">
                                Geç Gelen
                              </Badge>
                            )}
                            {record.checkOutTime && (
                              <Badge variant="secondary">
                                Çıkış Yapan
                              </Badge>
                            )}
                          </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
                          <div>
                            <span className="font-medium">Tarih:</span>
                            <p className="text-muted-foreground">
                              {new Date(record.date).toLocaleDateString('tr-TR')}
                            </p>
                          </div>
                          <div>
                            <span className="font-medium">Giriş Saati:</span>
                            <p className="text-muted-foreground">
                              {record.checkInTime ? 
                                new Date(record.checkInTime).toLocaleTimeString('tr-TR', {
                                  hour: '2-digit',
                                  minute: '2-digit'
                                }) : 
                                "Henüz giriş yapmadı"
                              }
                            </p>
                          </div>
                          <div>
                            <span className="font-medium">Çıkış Saati:</span>
                            <p className="text-muted-foreground">
                              {record.checkOutTime ? 
                                new Date(record.checkOutTime).toLocaleTimeString('tr-TR', {
                                  hour: '2-digit',
                                  minute: '2-digit'
                                }) : 
                                "Henüz çıkış yapmadı"
                              }
                            </p>
                          </div>
                          <div>
                            <span className="font-medium">Çalışma Süresi:</span>
                            <p className="text-muted-foreground">
                              {calculateWorkHours(record.checkInTime, record.checkOutTime)}
                            </p>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </MainLayout>
  );
}

client/src/pages/auth-page.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Redirect } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { InputOTP, InputOTPGroup, InputOTPSlot } from "@/components/ui/input-otp";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { insertUserSchema } from "@shared/schema";
import { z } from "zod";
import { Users, Building, Clock, Shield, Eye, EyeOff } from "lucide-react";
import { useQuery } from "@tanstack/react-query";

const loginSchema = z.object({
  phone: z.string().min(10, "Telefon numarası en az 10 karakter olmalıdır"),
  password: z.string().min(6, "Şifre en az 6 karakter olmalıdır"),
});

type LoginForm = z.infer<typeof loginSchema>;
type RegisterForm = z.infer<typeof insertUserSchema>;

export default function AuthPage() {
  const { user, loginMutation, registerMutation, verify2FAMutation } = useAuth();
  const [showTwoFactor, setShowTwoFactor] = useState(false);
  const [pendingUserId, setPendingUserId] = useState<string>("");
  const [twoFactorCode, setTwoFactorCode] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showRegisterPassword, setShowRegisterPassword] = useState(false);

  // Fetch branches for registration
  const { data: branches = [] } = useQuery({
    queryKey: ["/api/branches"],
    enabled: false, // Only fetch when needed
  });

  const loginForm = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      phone: "",
      password: "",
    },
  });

  const registerForm = useForm<RegisterForm>({
    resolver: zodResolver(insertUserSchema),
    defaultValues: {
      phone: "",
      password: "",
      name: "",
      role: "branch_admin",
      branchId: "",
      isActive: true,
    },
  });

  // Redirect if already logged in
  if (user) {
    return <Redirect to="/" />;
  }

  const handleLogin = (data: LoginForm) => {
    loginMutation.mutate(data, {
      onSuccess: (response) => {
        if (response.requiresTwoFactor) {
          setShowTwoFactor(true);
          setPendingUserId(response.userId);
        }
      },
    });
  };

  const handleRegister = (data: RegisterForm) => {
    registerMutation.mutate(data);
  };

  const handleTwoFactorSubmit = () => {
    if (twoFactorCode.length === 6) {
      verify2FAMutation.mutate({
        userId: pendingUserId,
        code: twoFactorCode,
      });
    }
  };

  const handleTwoFactorKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && twoFactorCode.length === 6) {
      handleTwoFactorSubmit();
    }
  };

  // Auto-submit when 2FA code is complete
  const handleTwoFactorChange = (value: string) => {
    setTwoFactorCode(value);
    if (value.length === 6) {
      setTimeout(() => {
        verify2FAMutation.mutate({
          userId: pendingUserId,
          code: value,
        });
      }, 100);
    }
  };

  if (showTwoFactor) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background p-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl">Doğrulama Kodu</CardTitle>
            <CardDescription>
              Telefon numaranıza gönderilen 6 haneli kodu girin
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="flex justify-center">
              <InputOTP
                maxLength={6}
                value={twoFactorCode}
                onChange={handleTwoFactorChange}
                onKeyDown={handleTwoFactorKeyPress}
                data-testid="input-2fa-code"
              >
                <InputOTPGroup>
                  <InputOTPSlot index={0} />
                  <InputOTPSlot index={1} />
                  <InputOTPSlot index={2} />
                  <InputOTPSlot index={3} />
                  <InputOTPSlot index={4} />
                  <InputOTPSlot index={5} />
                </InputOTPGroup>
              </InputOTP>
            </div>
            <Button 
              onClick={handleTwoFactorSubmit}
              className="w-full"
              disabled={twoFactorCode.length !== 6 || verify2FAMutation.isPending}
              data-testid="button-verify-2fa"
            >
              {verify2FAMutation.isPending ? "Doğrulanıyor..." : "Doğrula"}
            </Button>
            <Button
              variant="outline"
              onClick={() => {
                setShowTwoFactor(false);
                setTwoFactorCode("");
                setPendingUserId("");
              }}
              className="w-full"
              data-testid="button-back-to-login"
            >
              Geri Dön
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen grid lg:grid-cols-2">
      {/* Left side - Forms */}
      <div className="flex items-center justify-center p-8">
        <div className="w-full max-w-md space-y-6">
          <div className="text-center">
            <div className="flex items-center justify-center space-x-2 mb-4">
              <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
                <Users className="w-5 h-5 text-primary-foreground" />
              </div>
              <h1 className="text-2xl font-bold">PTS</h1>
            </div>
            <h2 className="text-xl text-muted-foreground">Personel Takip Sistemi</h2>
          </div>

          <Tabs defaultValue="login" className="space-y-4">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="login" data-testid="tab-login">Giriş Yap</TabsTrigger>
              <TabsTrigger value="register" data-testid="tab-register">Kayıt Ol</TabsTrigger>
            </TabsList>

            <TabsContent value="login">
              <Card>
                <CardHeader>
                  <CardTitle>Giriş Yap</CardTitle>
                  <CardDescription>
                    Telefon numaranız ve şifrenizle giriş yapın
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Form {...loginForm}>
                    <form onSubmit={loginForm.handleSubmit(handleLogin)} className="space-y-4">
                      <FormField
                        control={loginForm.control}
                        name="phone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Telefon Numarası</FormLabel>
                            <FormControl>
                              <Input
                                placeholder="05xxxxxxxxx"
                                {...field}
                                data-testid="input-login-phone"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={loginForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Şifre</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <Input
                                  type={showPassword ? "text" : "password"}
                                  placeholder="Şifrenizi girin"
                                  {...field}
                                  data-testid="input-login-password"
                                />
                                <Button
                                  type="button"
                                  variant="ghost"
                                  size="sm"
                                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                                  onClick={() => setShowPassword(!showPassword)}
                                  data-testid="button-toggle-password"
                                >
                                  {showPassword ? (
                                    <EyeOff className="h-4 w-4" />
                                  ) : (
                                    <Eye className="h-4 w-4" />
                                  )}
                                </Button>
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <Button
                        type="submit"
                        className="w-full"
                        disabled={loginMutation.isPending}
                        data-testid="button-login-submit"
                      >
                        {loginMutation.isPending ? "Giriş yapılıyor..." : "Giriş Yap"}
                      </Button>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="register">
              <Card>
                <CardHeader>
                  <CardTitle>Kayıt Ol</CardTitle>
                  <CardDescription>
                    Yeni hesap oluşturun
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Form {...registerForm}>
                    <form onSubmit={registerForm.handleSubmit(handleRegister)} className="space-y-4">
                      <FormField
                        control={registerForm.control}
                        name="name"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Ad Soyad</FormLabel>
                            <FormControl>
                              <Input
                                placeholder="Ad Soyad"
                                {...field}
                                data-testid="input-register-name"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={registerForm.control}
                        name="phone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Telefon Numarası</FormLabel>
                            <FormControl>
                              <Input
                                placeholder="05xxxxxxxxx"
                                {...field}
                                data-testid="input-register-phone"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={registerForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Şifre</FormLabel>
                            <FormControl>
                              <div className="relative">
                                <Input
                                  type={showRegisterPassword ? "text" : "password"}
                                  placeholder="Şifrenizi girin"
                                  {...field}
                                  data-testid="input-register-password"
                                />
                                <Button
                                  type="button"
                                  variant="ghost"
                                  size="sm"
                                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                                  onClick={() => setShowRegisterPassword(!showRegisterPassword)}
                                  data-testid="button-toggle-register-password"
                                >
                                  {showRegisterPassword ? (
                                    <EyeOff className="h-4 w-4" />
                                  ) : (
                                    <Eye className="h-4 w-4" />
                                  )}
                                </Button>
                              </div>
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={registerForm.control}
                        name="role"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Rol</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-register-role">
                                  <SelectValue placeholder="Rol seçin" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="super_admin">Süper Admin</SelectItem>
                                <SelectItem value="admin">Admin</SelectItem>
                                <SelectItem value="branch_admin">Şube Admin</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <Button
                        type="submit"
                        className="w-full"
                        disabled={registerMutation.isPending}
                        data-testid="button-register-submit"
                      >
                        {registerMutation.isPending ? "Kayıt yapılıyor..." : "Kayıt Ol"}
                      </Button>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </div>

      {/* Right side - Hero */}
      <div className="hidden lg:flex lg:items-center lg:justify-center bg-primary text-primary-foreground p-8">
        <div className="max-w-md text-center space-y-6">
          <h1 className="text-4xl font-bold">Personel Yönetimi</h1>
          <p className="text-lg text-primary-foreground/80">
            Kapsamlı personel takip ve yönetim sistemi ile işletmenizi daha verimli hale getirin.
          </p>

          <div className="grid grid-cols-2 gap-4 mt-8">
            <div className="text-center p-4">
              <Building className="w-8 h-8 mx-auto mb-2" />
              <h3 className="font-medium">Şube Yönetimi</h3>
              <p className="text-sm text-primary-foreground/70">Çoklu şube desteği</p>
            </div>
            <div className="text-center p-4">
              <Clock className="w-8 h-8 mx-auto mb-2" />
              <h3 className="font-medium">Vardiya Takibi</h3>
              <p className="text-sm text-primary-foreground/70">Otomatik vardiya yönetimi</p>
            </div>
            <div className="text-center p-4">
              <Users className="w-8 h-8 mx-auto mb-2" />
              <h3 className="font-medium">Personel Takibi</h3>
              <p className="text-sm text-primary-foreground/70">Kapsamlı personel bilgileri</p>
            </div>
            <div className="text-center p-4">
              <Shield className="w-8 h-8 mx-auto mb-2" />
              <h3 className="font-medium">Güvenli Giriş</h3>
              <p className="text-sm text-primary-foreground/70">2FA ile güvenli erişim</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

