    </div>
  );
}

client/src/pages/personnel-management.ts.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import MainLayout from "@/components/layout/main-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { PersonnelModal } from "@/components/modals/personnel-modal";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Personnel, InsertPersonnel, Branch } from "@shared/schema";
import { Plus, Search, Edit, Eye } from "lucide-react";

export default function PersonnelManagement() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedPersonnel, setSelectedPersonnel] = useState<Personnel | null>(null);
  const [modalMode, setModalMode] = useState<'view' | 'edit' | 'add'>('view');
  const [showModal, setShowModal] = useState(false);
  const { toast } = useToast();

  const { data: personnel = [], isLoading: personnelLoading } = useQuery<Personnel[]>({
    queryKey: ["/api/personnel", searchQuery ? { search: searchQuery } : {}],
  });

  const { data: branches = [] } = useQuery<Branch[]>({
    queryKey: ["/api/branches"],
  });

  const handleAddPersonnel = () => {
    setSelectedPersonnel(null);
    setModalMode('add');
    setShowModal(true);
  };

  const handleViewPersonnel = (person: Personnel) => {
    setSelectedPersonnel(person);
    setModalMode('view');
    setShowModal(true);
  };

  const handleEditPersonnel = (person: Personnel) => {
    setSelectedPersonnel(person);
    setModalMode('edit');
    setShowModal(true);
  };

  const filteredPersonnel = personnel.filter(p =>
    `${p.firstName} ${p.lastName}`.toLowerCase().includes(searchQuery.toLowerCase()) ||
    p.employeeNumber.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <MainLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold">Personel Yönetimi</h1>
          <Button onClick={handleAddPersonnel} data-testid="button-add-personnel">
            <Plus className="w-4 h-4 mr-2" />
            Yeni Personel
          </Button>
        </div>

        {/* Search */}
        <div className="flex items-center space-x-4">
          <div className="relative flex-1 max-w-sm">
            <Input
              placeholder="Personel ara..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
              data-testid="input-search-personnel"
            />
            <Search className="absolute left-3 top-2.5 w-4 h-4 text-muted-foreground" />
          </div>
        </div>

        {/* Personnel Table */}
        <Card>
          <CardContent className="p-0">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Personel Numarası</TableHead>
                  <TableHead>Ad Soyad</TableHead>
                  <TableHead>Pozisyon</TableHead>
                  <TableHead>Departman</TableHead>
                  <TableHead>Telefon</TableHead>
                  <TableHead>Şube</TableHead>
                  <TableHead>Durum</TableHead>
                  <TableHead className="text-right">İşlemler</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {personnelLoading ? (
                  // Loading skeletons
                  Array.from({ length: 5 }).map((_, i) => (
                    <TableRow key={i}>
                      <TableCell><div className="h-4 bg-muted rounded w-20 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-32 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-24 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-20 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-24 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-20 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-16 animate-pulse" /></TableCell>
                      <TableCell><div className="h-4 bg-muted rounded w-20 animate-pulse" /></TableCell>
                    </TableRow>
                  ))
                ) : filteredPersonnel.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={8} className="text-center py-12">
                      <p className="text-muted-foreground">Personel bulunamadı</p>
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredPersonnel.map((person) => (
                    <TableRow
                      key={person.id}
                      className="cursor-pointer hover:bg-muted/50"
                      onClick={() => handleViewPersonnel(person)}
                      data-testid={`row-personnel-${person.id}`}
                    >
                      <TableCell className="font-medium">{person.employeeNumber}</TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium">{person.firstName} {person.lastName}</div>
                          <div className="text-sm text-muted-foreground">{person.email}</div>
                        </div>
                      </TableCell>
                      <TableCell>{person.position}</TableCell>
                      <TableCell>{person.department || "Belirtilmemiş"}</TableCell>
                      <TableCell>{person.phone}</TableCell>
                      <TableCell>{branches.find(b => b.id === person.branchId)?.name || "Belirtilmemiş"}</TableCell>
                      <TableCell>
                        <Badge variant={person.isActive ? "default" : "secondary"}>
                          {person.isActive ? "Aktif" : "Pasif"}
                        </Badge>
                      </TableCell>
                      <TableCell className="text-right">
                        <div className="flex space-x-2 justify-end">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleViewPersonnel(person);
                            }}
                            data-testid={`button-view-${person.id}`}
                          >
                            <Eye className="w-4 h-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEditPersonnel(person);
                            }}
                            data-testid={`button-edit-${person.id}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      </div>

      {/* Personnel Modal */}
      <PersonnelModal
        personnel={selectedPersonnel}
        open={showModal}
        onOpenChange={setShowModal}
        mode={modalMode}
      />
    </MainLayout>
  );
}

client/src/pages/qr-control.tsx.content:
import React, { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { QrCode, Scan, CheckCircle, XCircle, Clock, User } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import MainLayout from "@/components/layout/main-layout";
import type { Personnel, Attendance } from "@shared/schema";

export default function QRControl() {
  const [qrCode, setQrCode] = useState("");
  const [scanResult, setScanResult] = useState<string | null>(null);
  const { toast } = useToast();

  const { data: personnel = [] } = useQuery<Personnel[]>({
    queryKey: ["/api/personnel"],
  });

  const { data: todayAttendance = [], refetch: refetchAttendance } = useQuery<Attendance[]>({
    queryKey: ["/api/attendance/today"],
  });

  const scanQRMutation = useMutation({
    mutationFn: async (qrData: string) => {
      const res = await apiRequest("POST", "/api/qr-scan", { qrCode: qrData });
      return await res.json();
    },
    onSuccess: (data) => {
      setScanResult(`QR Kod başarıyla okundu: ${data.message}`);
      queryClient.invalidateQueries({ queryKey: ["/api/attendance"] });
      refetchAttendance();
      toast({
        title: "Başarılı",
        description: data.message,
      });
      setQrCode("");
    },
    onError: (error: Error) => {
      setScanResult(`Hata: ${error.message}`);
      toast({
        title: "Hata",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleScan = () => {
    if (!qrCode.trim()) {
      toast({
        title: "Hata",
        description: "Lütfen QR kodu girin",
        variant: "destructive",
      });
      return;
    }
    scanQRMutation.mutate(qrCode.trim());
  };

  const stats = {
    totalPresent: todayAttendance.filter(a => a.checkInTime && !a.checkOutTime).length,
    totalCheckedOut: todayAttendance.filter(a => a.checkInTime && a.checkOutTime).length,
    totalPersonnel: personnel.filter(p => p.isActive).length,
  };

  return (
    <MainLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">QR Kod Kontrol</h1>
            <p className="text-muted-foreground">Personel giriş-çıkış QR kodlarını okutun</p>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid gap-4 md:grid-cols-3">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">İş Yerinde</CardTitle>
              <User className="h-4 w-4 text-green-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600" data-testid="text-present-count">
                {stats.totalPresent}
              </div>
              <p className="text-xs text-muted-foreground">Aktif personel</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Çıkış Yapan</CardTitle>
              <CheckCircle className="h-4 w-4 text-blue-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-600" data-testid="text-checked-out-count">
                {stats.totalCheckedOut}
              </div>
              <p className="text-xs text-muted-foreground">Bugün çıkış yapan</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Toplam Personel</CardTitle>
              <User className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-personnel">
                {stats.totalPersonnel}
              </div>
              <p className="text-xs text-muted-foreground">Aktif personel sayısı</p>
            </CardContent>
          </Card>
        </div>

        {/* QR Scanner */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <QrCode className="mr-2 h-5 w-5" />
              QR Kod Okuyucu
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex space-x-2">
              <Input
                placeholder="QR kodu buraya girin veya okutun..."
                value={qrCode}
                onChange={(e) => setQrCode(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleScan()}
                data-testid="input-qr-code"
              />
              <Button 
                onClick={handleScan}
                disabled={scanQRMutation.isPending}
                data-testid="button-scan-qr"
              >
                <Scan className="mr-2 h-4 w-4" />
                {scanQRMutation.isPending ? "Okutluyor..." : "Okut"}
              </Button>
            </div>

            {scanResult && (
              <Alert className={scanResult.startsWith("Hata") ? "border-red-200 bg-red-50" : "border-green-200 bg-green-50"}>
                <AlertDescription data-testid="text-scan-result">
                  {scanResult}
                </AlertDescription>
              </Alert>
            )}

            <div className="text-sm text-muted-foreground">
              <p>• Personel QR kodunu okutarak giriş-çıkış kaydı oluşturabilirsiniz</p>
              <p>• QR kod formatı: PERSONEL_ID format</p>
              <p>• Sistem otomatik olarak giriş/çıkış durumunu tespit eder</p>
            </div>
          </CardContent>
        </Card>

        {/* Today's Attendance */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Clock className="mr-2 h-5 w-5" />
              Bugünkü Devam Durumu
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {todayAttendance.length === 0 ? (
                <div className="text-center py-8">
                  <Clock className="mx-auto h-12 w-12 text-muted-foreground" />
                  <h3 className="mt-4 text-lg font-semibold">Bugün henüz devam kaydı yok</h3>
                  <p className="text-muted-foreground">
                    İlk QR kod okutmalarını bekliyoruz.
                  </p>
                </div>
              ) : (
                <div className="grid gap-4">
                  {todayAttendance.map((attendance) => {
                    const person = personnel.find(p => p.id === attendance.personnelId);
                    const fullName = person ? `${person.firstName} ${person.lastName}` : "Bilinmeyen";
                    const isPresent = attendance.checkInTime && !attendance.checkOutTime;

                    return (
                      <div 
                        key={attendance.id} 
                        className="border rounded-lg p-4 flex items-center justify-between"
                        data-testid={`card-attendance-${attendance.id}`}
                      >
                        <div className="flex items-center space-x-4">
                          <div>
                            <h3 className="font-semibold">{fullName}</h3>
                            <p className="text-sm text-muted-foreground">
                              {person?.employeeNumber || "N/A"} • {person?.position || "N/A"}
                            </p>
                          </div>
                        </div>

                        <div className="flex items-center space-x-4">
                          <div className="text-right text-sm">
                            {attendance.checkInTime && (
                              <p>
                                <span className="font-medium">Giriş:</span> {' '}
                                {new Date(attendance.checkInTime).toLocaleTimeString('tr-TR', {
                                  hour: '2-digit',
                                  minute: '2-digit'
                                })}
                              </p>
                            )}
                            {attendance.checkOutTime && (
                              <p>
                                <span className="font-medium">Çıkış:</span> {' '}
                                {new Date(attendance.checkOutTime).toLocaleTimeString('tr-TR', {
                                  hour: '2-digit',
                                  minute: '2-digit'
                                })}
                              </p>
                            )}
                          </div>

                          <Badge 
                            variant={isPresent ? "default" : "secondary"}
                            className={isPresent ? "bg-green-500 hover:bg-green-600" : ""}
                          >
                            {isPresent ? (
                              <>
                                <CheckCircle className="mr-1 h-3 w-3" />
                                İş Yerinde
                              </>
                            ) : (
                              <>
                                <XCircle className="mr-1 h-3 w-3" />
                                Çıkış Yapan
                              </>
                            )}
                          </Badge>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </MainLayout>
  );
}

client/src/pages/shift-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import MainLayout from "@/components/layout/main-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { DetailModal } from "@/components/modals/detail-modal";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Shift, InsertShift, insertShiftSchema, Branch } from "@shared/schema";
import { Plus, Clock, Sunrise, Sun, Sunset, Moon, Edit, Eye } from "lucide-react";
import { z } from "zod";

const shiftFormSchema = insertShiftSchema.extend({
  name: z.string().min(2, "Vardiya adı en az 2 karakter olmalıdır"),
  startTime: z.string().regex(/^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/, "Geçerli saat formatı: HH:MM"),
  endTime: z.string().regex(/^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/, "Geçerli saat formatı: HH:MM"),
});

type ShiftForm = z.infer<typeof shiftFormSchema>;

const shiftTypeIcons = {
  sabah: Sunrise,
  oglen: Sun,
  aksam: Sunset,
  gece: Moon,
};

const shiftTypeLabels = {
  sabah: "Sabah",
  oglen: "Öğlen",
  aksam: "Akşam",
  gece: "Gece",
};

const shiftTypeColors = {
  sabah: "text-orange-500",
  oglen: "text-yellow-500",
  aksam: "text-purple-500",
  gece: "text-blue-500",
};

export default function ShiftManagement() {
  const [selectedShift, setSelectedShift] = useState<Shift | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const { toast } = useToast();
  const { user } = useAuth();

  const { data: shifts = [], isLoading: shiftsLoading } = useQuery<Shift[]>({
    queryKey: ["/api/shifts"],
  });

  const { data: branches = [] } = useQuery<Branch[]>({
    queryKey: ["/api/branches"],
  });

  const form = useForm<ShiftForm>({
    resolver: zodResolver(shiftFormSchema),
    defaultValues: {
      name: "",
      type: "sabah",
      startTime: "",
      endTime: "",
      branchId: "",
      isActive: true,
    },
  });

  const createShiftMutation = useMutation({
    mutationFn: async (data: InsertShift) => {
      const res = await apiRequest("POST", "/api/shifts", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/shifts"] });
      setShowAddModal(false);
      form.reset();
      toast({
        title: "Başarılı",
        description: "Vardiya başarıyla eklendi",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Hata",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateShiftMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<Shift> }) => {
      const res = await apiRequest("PUT", `/api/shifts/${id}`, data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/shifts"] });
      setShowEditModal(false);
      setSelectedShift(null);
      form.reset();
      toast({
        title: "Başarılı",
        description: "Vardiya başarıyla güncellendi",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Hata",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleAddShift = (data: ShiftForm) => {
    createShiftMutation.mutate(data);
  };

  const handleEditShift = (data: ShiftForm) => {
    if (selectedShift) {
      updateShiftMutation.mutate({
        id: selectedShift.id,
        data,
      });
    }
  };

  const openAddModal = () => {
    form.reset();
    if (user?.role === 'branch_admin' && user.branchId) {
      form.setValue('branchId', user.branchId);
    }
    setShowAddModal(true);
  };

  const openEditModal = (shift: Shift) => {
    setSelectedShift(shift);
    form.reset({
      name: shift.name,
      type: shift.type,
      startTime: shift.startTime,
      endTime: shift.endTime,
      branchId: shift.branchId,
      isActive: shift.isActive,
    });
    setShowEditModal(true);
  };

  const getBranchName = (branchId: string) => {
    const branch = branches.find(b => b.id === branchId);
    return branch?.name || "Bilinmeyen Şube";
  };

  const canManageShifts = user?.role === 'super_admin' || user?.role === 'admin' || user?.role === 'branch_admin';
  const availableBranches = user?.role === 'branch_admin' && user.branchId
    ? branches.filter(b => b.id === user.branchId)
    : branches;

  return (
    <MainLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold">Vardiya Yönetimi</h1>
            <p className="text-muted-foreground mt-1">
              Çalışma vardiyalarını oluşturun ve yönetin
            </p>
          </div>
          {canManageShifts && (
            <Button onClick={openAddModal} data-testid="button-add-shift">
              <Plus className="w-4 h-4 mr-2" />
              Yeni Vardiya
            </Button>
          )}
        </div>

        {/* Shifts Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {shiftsLoading ? (
            // Loading skeletons
            Array.from({ length: 6 }).map((_, i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="p-6">
                  <div className="space-y-3">
                    <div className="h-4 bg-muted rounded w-3/4" />
                    <div className="h-3 bg-muted rounded w-1/2" />
                    <div className="h-3 bg-muted rounded w-2/3" />
                  </div>
                </CardContent>
              </Card>
            ))
          ) : shifts.length === 0 ? (
            <div className="col-span-full text-center py-12">
              <Clock className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
              <p className="text-muted-foreground">Henüz vardiya eklenmemiş</p>
              {canManageShifts && (
                <Button onClick={openAddModal} className="mt-4" data-testid="button-add-first-shift">
                  <Plus className="w-4 h-4 mr-2" />
                  İlk Vardiyayı Ekle
                </Button>
              )}
            </div>
          ) : (
            shifts.map((shift) => {
              const IconComponent = shiftTypeIcons[shift.type];
              const typeColor = shiftTypeColors[shift.type];
              const typeLabel = shiftTypeLabels[shift.type];

              return (
                <Card
                  key={shift.id}
                  className="cursor-pointer hover:shadow-lg transition-shadow"
                  onClick={() => setSelectedShift(shift)}
                  data-testid={`card-shift-${shift.id}`}
                >
                  <CardContent className="p-6">
                    <div className="space-y-4">
                      <div className="flex items-start justify-between">
                        <div className="flex items-start space-x-3">
                          <div className={`w-10 h-10 ${typeColor.replace('text-', 'bg-')}/10 rounded-lg flex items-center justify-center`}>
                            <IconComponent className={`w-5 h-5 ${typeColor}`} />
                          </div>
                          <div>
                            <h3 className="font-semibold text-lg">{shift.name}</h3>
                            <Badge variant="outline" className="mt-1">
                              {typeLabel}
                            </Badge>
                          </div>
                        </div>
                        <Badge variant={shift.isActive ? "default" : "secondary"}>
                          {shift.isActive ? "Aktif" : "Pasif"}
                        </Badge>
                      </div>

                      <div className="space-y-2">
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-muted-foreground">Başlangıç:</span>
                          <span className="font-medium">{shift.startTime}</span>
                        </div>
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-muted-foreground">Bitiş:</span>
                          <span className="font-medium">{shift.endTime}</span>
                        </div>
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-muted-foreground">Şube:</span>
                          <span className="font-medium">{getBranchName(shift.branchId)}</span>
                        </div>
                      </div>

                      <div className="flex space-x-2 pt-2">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={(e) => {
                            e.stopPropagation();
                            setSelectedShift(shift);
                          }}
                          data-testid={`button-view-${shift.id}`}
                        >
                          <Eye className="w-4 h-4 mr-1" />
                          Görüntüle
                        </Button>
                        {canManageShifts && (
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              openEditModal(shift);
                            }}
                            data-testid={`button-edit-${shift.id}`}
                          >
                            <Edit className="w-4 h-4 mr-1" />
                            Düzenle
                          </Button>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })
          )}
        </div>
      </div>

      {/* Shift Detail Modal */}
      <DetailModal
        open={!!selectedShift && !showEditModal}
        onOpenChange={(open) => !open && setSelectedShift(null)}
        title="Vardiya Detayları"
      >
        {selectedShift && (
          <div className="space-y-6">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm font-medium text-muted-foreground">Vardiya Adı</label>
                <p className="mt-1 text-foreground">{selectedShift.name}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">Tip</label>
                <Badge variant="outline" className="mt-1">
                  {shiftTypeLabels[selectedShift.type]}
                </Badge>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">Başlangıç Saati</label>
                <p className="mt-1 text-foreground">{selectedShift.startTime}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">Bitiş Saati</label>
                <p className="mt-1 text-foreground">{selectedShift.endTime}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">Şube</label>
                <p className="mt-1 text-foreground">{getBranchName(selectedShift.branchId)}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">Durum</label>
                <Badge variant={selectedShift.isActive ? "default" : "secondary"} className="mt-1">
                  {selectedShift.isActive ? "Aktif" : "Pasif"}
                </Badge>
              </div>
              <div>
                <label className="text-sm font-medium text-muted-foreground">Oluşturulma Tarihi</label>
                <p className="mt-1 text-foreground">
                  {new Date(selectedShift.createdAt).toLocaleDateString('tr-TR')}
                </p>
              </div>
            </div>

            {canManageShifts && (
              <div className="flex space-x-3">
                <Button
                  onClick={() => openEditModal(selectedShift)}
                  data-testid="button-edit-shift"
                >
                  Düzenle
                </Button>
                <Button variant="secondary" data-testid="button-assign-personnel">
                  Personel Atama
                </Button>
              </div>
            )}
          </div>
        )}
      </DetailModal>

      {/* Add/Edit Shift Modal */}
      <DetailModal
        open={showAddModal || showEditModal}
        onOpenChange={(open) => {
          if (!open) {
            setShowAddModal(false);
            setShowEditModal(false);
            setSelectedShift(null);
            form.reset();
          }
        }}
        title={showEditModal ? "Vardiya Düzenle" : "Yeni Vardiya Ekle"}
      >
        <Form {...form}>
          <form
            onSubmit={form.handleSubmit(showEditModal ? handleEditShift : handleAddShift)}
            className="space-y-6"
          >
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Vardiya Adı *</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Vardiya adını girin"
                      {...field}
                      data-testid="input-shift-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="type"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Vardiya Tipi *</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-shift-type">
                        <SelectValue placeholder="Vardiya tipini seçin" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="sabah">Sabah</SelectItem>
                      <SelectItem value="oglen">Öğlen</SelectItem>
                      <SelectItem value="aksam">Akşam</SelectItem>
                      <SelectItem value="gece">Gece</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="startTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Başlangıç Saati *</FormLabel>
                    <FormControl>
                      <Input
                        type="time"
                        {...field}
                        data-testid="input-shift-start-time"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="endTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Bitiş Saati *</FormLabel>
                    <FormControl>
                      <Input
                        type="time"
                        {...field}
                        data-testid="input-shift-end-time"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="branchId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Şube *</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-shift-branch">
                        <SelectValue placeholder="Şube seçin" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {availableBranches.map((branch) => (
                        <SelectItem key={branch.id} value={branch.id}>
                          {branch.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex space-x-3">
              <Button
                type="submit"
                disabled={createShiftMutation.isPending || updateShiftMutation.isPending}
                data-testid="button-submit-shift"
              >
                {createShiftMutation.isPending || updateShiftMutation.isPending
                  ? "Kaydediliyor..."
                  : showEditModal
                  ? "Güncelle"
                  : "Ekle"}
              </Button>
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  setShowAddModal(false);
                  setShowEditModal(false);
                  setSelectedShift(null);
                  form.reset();
                }}
                data-testid="button-cancel-shift"
              >
                İptal
              </Button>
            </div>
          </form>
        </Form>
      </DetailModal>
    </MainLayout>
  );
}

client/src/pages/system-settings.tsx.content:
import React, { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Settings, Save, Bell, Shield, Database, Mail, Phone, Clock } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import MainLayout from "@/components/layout/main-layout";

interface SystemSettings {
  id?: string;
  companyName: string;
  companyAddress: string;
  companyPhone: string;
  companyEmail: string;
  workHours: {
    start: string;
    end: string;
    lunchBreak: number;
  };
  notifications: {
    emailEnabled: boolean;
    smsEnabled: boolean;
    lateArrivalAlert: boolean;
    absenceAlert: boolean;
  };
  attendance: {
    graceMinutes: number;
    autoClockOut: boolean;
    requireLocationCheck: boolean;
  };
  backup: {
    autoBackup: boolean;
    backupFrequency: string;
    retentionDays: number;
  };
}

export default function SystemSettings() {
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("general");

  const { data: settings, isLoading } = useQuery<SystemSettings>({
    queryKey: ["/api/settings"],
  });

  const [formData, setFormData] = useState<SystemSettings>({
    companyName: "",
    companyAddress: "",
    companyPhone: "",
    companyEmail: "",
    workHours: {
      start: "09:00",
      end: "18:00",
      lunchBreak: 60,
    },
    notifications: {
      emailEnabled: true,
      smsEnabled: true,
      lateArrivalAlert: true,
      absenceAlert: true,
    },
    attendance: {
      graceMinutes: 15,
      autoClockOut: false,
      requireLocationCheck: false,
    },
    backup: {
      autoBackup: true,
      backupFrequency: "daily",
      retentionDays: 30,
    },
  });

  // Initialize form data when settings are loaded
  React.useEffect(() => {
    if (settings) {
      setFormData(settings);
    }
  }, [settings]);

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: SystemSettings) => {
      const res = await apiRequest("PUT", "/api/settings", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Başarılı",
        description: "Sistem ayarları güncellendi",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Hata",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    updateSettingsMutation.mutate(formData);
  };

  const updateFormData = (section: keyof SystemSettings, field: string, value: any) => {
    setFormData(prev => ({
      ...prev,
      [section]: typeof prev[section] === 'object' ? {
        ...prev[section],
        [field]: value
      } : value
    }));
  };

  if (isLoading) {
    return (
      <MainLayout>
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-muted-foreground">Sistem ayarları yükleniyor...</p>
          </div>
        </div>
      </MainLayout>
    );
  }

  return (
    <MainLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">Sistem Ayarları</h1>
            <p className="text-muted-foreground">Sistem konfigürasyonlarını yönetin</p>
          </div>
          <Button 
            onClick={handleSave}
            disabled={updateSettingsMutation.isPending}
            data-testid="button-save-settings"
          >
            <Save className="mr-2 h-4 w-4" />
            {updateSettingsMutation.isPending ? "Kaydediliyor..." : "Ayarları Kaydet"}
          </Button>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
          <TabsList className="grid grid-cols-4 w-full">
            <TabsTrigger value="general">Genel</TabsTrigger>
            <TabsTrigger value="attendance">Devam</TabsTrigger>
            <TabsTrigger value="notifications">Bildirimler</TabsTrigger>
            <TabsTrigger value="backup">Yedekleme</TabsTrigger>
          </TabsList>

          <TabsContent value="general" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Settings className="mr-2 h-5 w-5" />
                  Şirket Bilgileri
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="companyName">Şirket Adı</Label>
                    <Input
                      id="companyName"
                      value={formData.companyName}
                      onChange={(e) => setFormData(prev => ({ ...prev, companyName: e.target.value }))}
                      data-testid="input-company-name"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="companyPhone">Şirket Telefonu</Label>
                    <Input
                      id="companyPhone"
                      value={formData.companyPhone}
                      onChange={(e) => setFormData(prev => ({ ...prev, companyPhone: e.target.value }))}
                      data-testid="input-company-phone"
                    />
                  </div>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="companyAddress">Şirket Adresi</Label>
                  <Input
                    id="companyAddress"
                    value={formData.companyAddress}
                    onChange={(e) => setFormData(prev => ({ ...prev, companyAddress: e.target.value }))}
                    data-testid="input-company-address"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="companyEmail">Şirket E-posta</Label>
                  <Input
                    id="companyEmail"
                    type="email"
                    value={formData.companyEmail}
                    onChange={(e) => setFormData(prev => ({ ...prev, companyEmail: e.target.value }))}
                    data-testid="input-company-email"
                  />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Clock className="mr-2 h-5 w-5" />
                  Çalışma Saatleri
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="workStart">Başlama Saati</Label>
                    <Input
                      id="workStart"
                      type="time"
                      value={formData.workHours.start}
                      onChange={(e) => updateFormData('workHours', 'start', e.target.value)}
                      data-testid="input-work-start"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="workEnd">Bitiş Saati</Label>
                    <Input
                      id="workEnd"
                      type="time"
                      value={formData.workHours.end}
                      onChange={(e) => updateFormData('workHours', 'end', e.target.value)}
                      data-testid="input-work-end"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="lunchBreak">Öğle Molası (dakika)</Label>
                    <Input
                      id="lunchBreak"
                      type="number"
                      value={formData.workHours.lunchBreak}
                      onChange={(e) => updateFormData('workHours', 'lunchBreak', parseInt(e.target.value))}
                      data-testid="input-lunch-break"
                    />
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="attendance" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Shield className="mr-2 h-5 w-5" />
                  Devam Takibi Ayarları
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="graceMinutes">Hoşgörü Süresi (dakika)</Label>
                  <Input
                    id="graceMinutes"
                    type="number"
                    value={formData.attendance.graceMinutes}
                    onChange={(e) => updateFormData('attendance', 'graceMinutes', parseInt(e.target.value))}
                    data-testid="input-grace-minutes"
                  />
                  <p className="text-sm text-muted-foreground">
                    Geç gelme olarak sayılmadan önce tolerans süresi
                  </p>
                </div>

                <Separator />

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Otomatik Çıkış</Label>
                    <p className="text-sm text-muted-foreground">
                      Mesai saati sonunda otomatik çıkış kaydı oluştur
                    </p>
                  </div>
                  <Switch
                    checked={formData.attendance.autoClockOut}
                    onCheckedChange={(checked) => updateFormData('attendance', 'autoClockOut', checked)}
                    data-testid="switch-auto-clock-out"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Konum Kontrolü</Label>
                    <p className="text-sm text-muted-foreground">
                      QR kod okutmalarında konum doğrulaması yap
                    </p>
                  </div>
                  <Switch
                    checked={formData.attendance.requireLocationCheck}
                    onCheckedChange={(checked) => updateFormData('attendance', 'requireLocationCheck', checked)}
                    data-testid="switch-location-check"
                  />
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="notifications" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Bell className="mr-2 h-5 w-5" />
                  Bildirim Ayarları
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label className="flex items-center">
                      <Mail className="mr-2 h-4 w-4" />
                      E-posta Bildirimleri
                    </Label>
                    <p className="text-sm text-muted-foreground">
                      Sistem bildirimlerini e-posta ile gönder
                    </p>
                  </div>
                  <Switch
                    checked={formData.notifications.emailEnabled}
                    onCheckedChange={(checked) => updateFormData('notifications', 'emailEnabled', checked)}
                    data-testid="switch-email-notifications"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label className="flex items-center">
                      <Phone className="mr-2 h-4 w-4" />
                      SMS Bildirimleri
                    </Label>
                    <p className="text-sm text-muted-foreground">
                      Sistem bildirimlerini SMS ile gönder
                    </p>
                  </div>
                  <Switch
                    checked={formData.notifications.smsEnabled}
                    onCheckedChange={(checked) => updateFormData('notifications', 'smsEnabled', checked)}
                    data-testid="switch-sms-notifications"
                  />
                </div>

                <Separator />

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Geç Gelme Uyarısı</Label>
                    <p className="text-sm text-muted-foreground">
                      Personel geç geldiğinde bildirim gönder
                    </p>
                  </div>
                  <Switch
                    checked={formData.notifications.lateArrivalAlert}
                    onCheckedChange={(checked) => updateFormData('notifications', 'lateArrivalAlert', checked)}
                    data-testid="switch-late-arrival-alert"
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Devamsızlık Uyarısı</Label>
                    <p className="text-sm text-muted-foreground">
                      Personel hiç gelmediğinde bildirim gönder
                    </p>
                  </div>
                  <Switch
                    checked={formData.notifications.absenceAlert}
                    onCheckedChange={(checked) => updateFormData('notifications', 'absenceAlert', checked)}
                    data-testid="switch-absence-alert"
                  />
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="backup" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Database className="mr-2 h-5 w-5" />
                  Yedekleme Ayarları
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Otomatik Yedekleme</Label>
                    <p className="text-sm text-muted-foreground">
                      Sistem verilerini otomatik olarak yedekle
                    </p>
                  </div>
                  <Switch
                    checked={formData.backup.autoBackup}
                    onCheckedChange={(checked) => updateFormData('backup', 'autoBackup', checked)}
                    data-testid="switch-auto-backup"
                  />
                </div>

                {formData.backup.autoBackup && (
                  <>
                    <div className="space-y-2">
                      <Label htmlFor="backupFrequency">Yedekleme Sıklığı</Label>
                      <select
                        id="backupFrequency"
                        className="w-full p-2 border rounded-md"
                        value={formData.backup.backupFrequency}
                        onChange={(e) => updateFormData('backup', 'backupFrequency', e.target.value)}
                        data-testid="select-backup-frequency"
                      >
                        <option value="daily">Günlük</option>
                        <option value="weekly">Haftalık</option>
                        <option value="monthly">Aylık</option>
                      </select>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="retentionDays">Saklama Süresi (gün)</Label>
                      <Input
                        id="retentionDays"
                        type="number"
                        value={formData.backup.retentionDays}
                        onChange={(e) => updateFormData('backup', 'retentionDays', parseInt(e.target.value))}
                        data-testid="input-retention-days"
                      />
                      <p className="text-sm text-muted-foreground">
                        Eski yedekler bu süre sonunda silinir
                      </p>
                    </div>
                  </>
                )}

                <Separator />

                <div className="space-y-2">
                  <Button variant="outline" className="w-full" data-testid="button-manual-backup">
                    <Database className="mr-2 h-4 w-4" />
                    Manuel Yedekleme Başlat
                  </Button>
                  <p className="text-sm text-muted-foreground text-center">
                    Son yedekleme: Henüz yedekleme yapılmadı
                  </p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </MainLayout>
  );
}

client/src/components/layout/header.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useAuth } from "@/hooks/use-auth";
import { Menu, Search, Bell, ChevronDown, LogOut } from "lucide-react";

interface HeaderProps {
  onMenuClick: () => void;
  onToggleSidebar: () => void;
  sidebarCollapsed: boolean;
}

export default function Header({ onMenuClick, onToggleSidebar, sidebarCollapsed }: HeaderProps) {
  const { user, logoutMutation } = useAuth();
  const [searchQuery, setSearchQuery] = useState("");

  const userInitials = user?.name
    ?.split(" ")
    .map(n => n[0])
    .join("")
    .toUpperCase() || "??";

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  return (
    <header className="bg-card border-b border-border">
      <div className="flex items-center justify-between h-16 px-4">
        <div className="flex items-center space-x-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={onMenuClick}
            className="md:hidden"
            data-testid="button-open-sidebar"
          >
            <Menu className="w-5 h-5" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={onToggleSidebar}
            className="hidden md:block"
            data-testid="button-toggle-sidebar"
          >
            <Menu className="w-5 h-5" />
          </Button>
          <h1 className="text-xl font-semibold">Dashboard</h1>
        </div>

        <div className="flex items-center space-x-4">
          {/* Search */}
          <div className="hidden sm:block">
            <div className="relative">
              <Input
                type="search"
                placeholder="Personel ara..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-64 pl-10"
                data-testid="input-search"
              />
              <Search className="absolute left-3 top-2.5 w-4 h-4 text-muted-foreground" />
            </div>
          </div>

          {/* Notifications */}
          <Button variant="ghost" size="sm" className="relative" data-testid="button-notifications">
            <Bell className="w-5 h-5" />
            <span className="absolute -top-1 -right-1 w-3 h-3 bg-destructive rounded-full" />
          </Button>

          {/* User Menu */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="flex items-center space-x-2" data-testid="button-user-menu">
                <div className="w-8 h-8 bg-accent rounded-full flex items-center justify-center">
                  <span className="text-sm font-medium text-accent-foreground">
                    {userInitials}
                  </span>
                </div>
                <ChevronDown className="w-4 h-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem 
                onClick={handleLogout}
                disabled={logoutMutation.isPending}
                data-testid="button-logout"
              >
                <LogOut className="w-4 h-4 mr-2" />
                Çıkış Yap
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </header>
  );
}

